mapper.select:
select id, pw, email, name, nickname, profile from users where

	id = #{id}


	and pw = #{pw}


	and email = #{email}


	nickname = #{nickname}

select id, email, name, nickname, profile from users where 

	email = #{email}


	and name = #{name}

mapper.update:
update users set 

	profile = #{profile}


	, email = #{email}


	, nickname = #{nickname} 


	, pw = #{pw}

where id = #{id}
